pragma solidity >=0.8.0;
pragma experimental ABIEncoderV2;

import { EIP20Interface } from "./EIP20Interface.sol";

interface IERC4626 is EIP20Interface {
  /*///////////////////////////////////////////////////////////////
                                Events
    //////////////////////////////////////////////////////////////*/

  event Deposit(address indexed from, address indexed to, uint256 value);

  event Withdraw(address indexed from, address indexed to, uint256 value);

  /*///////////////////////////////////////////////////////////////
                            Mutable Functions
    //////////////////////////////////////////////////////////////*/

  /**
      @notice Deposit a specific amount of underlying tokens.
      @param to The address to receive shares corresponding to the deposit
      @param underlyingAmount The amount of the underlying token to deposit.
      @return shares The shares in the vault credited to `to`
    */
  function deposit(address to, uint256 underlyingAmount) external returns (uint256 shares);

  /**
      @notice Mint an exact amount of shares for a variable amount of underlying tokens.
      @param to The address to receive shares corresponding to the mint.
      @param shareAmount The amount of vault shares to mint.
      @return underlyingAmount The amount of the underlying tokens deposited from the mint call.
    */
  function mint(address to, uint256 shareAmount) external returns (uint256 underlyingAmount);

  /**
      @notice Withdraw a specific amount of underlying tokens.
      @param from The address to burn shares from corresponding to the withdrawal.
      @param to The address to receive underlying corresponding to the withdrawal.
      @param underlyingAmount The amount of the underlying token to withdraw.
      @return shares The shares in the vault burned from sender
    */
  function withdraw(
    address from,
    address to,
    uint256 underlyingAmount
  ) external returns (uint256 shares);

  /**
      @notice Redeem a specific amount of shares for underlying tokens.
      @param from The address to burn shares from corresponding to the redemption.
      @param to The address to receive underlying corresponding to the redemption.
      @param shareAmount The amount of shares to redeem.
      @return value The underlying amount transferred to `to`.
    */
  function redeem(
    address from,
    address to,
    uint256 shareAmount
  ) external returns (uint256 value);

  /*///////////////////////////////////////////////////////////////
                            View Functions
    //////////////////////////////////////////////////////////////*/

  /** 
      @notice The underlying token the Vault accepts.
      @return the ERC20 underlying implementation address.
    */
  function underlying() external view returns (EIP20Interface);

  /** 
      @notice Returns a user's Vault balance in underlying tokens.
      @param user The user to get the underlying balance of.
      @return balance The user's Vault balance in underlying tokens.
    */
  function balanceOfUnderlying(address user) external view returns (uint256 balance);

  /** 
      @notice Calculates the total amount of underlying tokens the Vault manages.
      @return The total amount of underlying tokens the Vault manages.
    */
  function totalUnderlying() external view returns (uint256);

  /** 
      @notice Returns the value in underlying terms of one vault token. 
     */
  function exchangeRate() external view returns (uint256);

  /**
      @notice Returns the amount of vault tokens that would be obtained if depositing a given amount of underlying tokens in a `deposit` call.
      @param underlyingAmount the input amount of underlying tokens
      @return shareAmount the corresponding amount of shares out from a deposit call with `underlyingAmount` in
     */
  function previewDeposit(uint256 underlyingAmount) external view returns (uint256 shareAmount);

  /**
      @notice Returns the amount of underlying tokens that would be deposited if minting a given amount of shares in a `mint` call.
      @param shareAmount the amount of shares from a mint call.
      @return underlyingAmount the amount of underlying tokens corresponding to the mint call
     */
  function previewMint(uint256 shareAmount) external view returns (uint256 underlyingAmount);

  /**
      @notice Returns the amount of vault tokens that would be burned if withdrawing a given amount of underlying tokens in a `withdraw` call.
      @param underlyingAmount the input amount of underlying tokens
      @return shareAmount the corresponding amount of shares out from a withdraw call with `underlyingAmount` in
     */
  function previewWithdraw(uint256 underlyingAmount) external view returns (uint256 shareAmount);

  /**
      @notice Returns the amount of underlying tokens that would be obtained if redeeming a given amount of shares in a `redeem` call.
      @param shareAmount the amount of shares from a redeem call.
      @return underlyingAmount the amount of underlying tokens corresponding to the redeem call
     */
  function previewRedeem(uint256 shareAmount) external view returns (uint256 underlyingAmount);
}

contracts/compound/CErc20PluginDelegate..content:
pragma solidity >=0.8.0;
pragma experimental ABIEncoderV2;

import "./CErc20Delegate.sol";
import "./EIP20Interface.sol";
import "./IERC4626.sol";

/**
 * @title Rari's CErc20Plugin's Contract
 * @notice CToken which outsources token logic to a plugin
 * @author Joey Santoro
 *
 * CErc20PluginDelegate deposits and withdraws from a plugin conract
 * It is also capable of delegating reward functionality to a PluginRewardsDistributor
 */
contract CErc20PluginDelegate is CErc20Delegate {
  /**
   * @notice Plugin address
   */
  IERC4626 public plugin;

  uint256 public constant PRECISION = 1e18;

  /**
   * @notice Delegate interface to become the implementation
   * @param data The encoded arguments for becoming
   */
  function _becomeImplementation(bytes calldata data) external virtual override {
    require(msg.sender == address(this) || hasAdminRights());

    address _plugin = abi.decode(data, (address));

    require(_plugin != address(0), "0 addr");

    if (address(plugin) != address(0)) {
      plugin.redeem(address(this), address(this), plugin.balanceOf(address(this)));
    }

    plugin = IERC4626(_plugin);

    uint256 amount = EIP20Interface(underlying).balanceOf(address(this));
    if (amount != 0) {
      deposit(amount);
    }
  }

  /*** CToken Overrides ***/

  /*** Safe Token ***/

  /**
   * @notice Gets balance of the plugin in terms of the underlying
   * @return The quantity of underlying tokens owned by this contract
   */
  function getCashPrior() internal view override returns (uint256) {
    return plugin.balanceOfUnderlying(address(this));
  }

  /**
   * @notice Transfer the underlying to the cToken and trigger a deposit
   * @param from Address to transfer funds from
   * @param amount Amount of underlying to transfer
   * @return The actual amount that is transferred
   */
  function doTransferIn(address from, uint256 amount) internal override returns (uint256) {
    // Perform the EIP-20 transfer in
    require(EIP20Interface(underlying).transferFrom(from, address(this), amount), "send fail");

    deposit(amount);
    return amount;
  }

  /**
   * @notice Deposit the underlying in the plugin
   * @param amount Amount of underlying to deposit
   */
  function deposit(uint256 amount) internal {
    EIP20Interface(underlying).approve(address(plugin), amount);

    plugin.deposit(address(this), amount);
  }

  /**
   * @notice Transfer the underlying from plugin to destination
   * @param to Address to transfer funds to
   * @param amount Amount of underlying to transfer
   */
  function doTransferOut(address payable to, uint256 amount) internal {
    plugin.withdraw(address(this), to, amount);
  }
}

contracts/compound/CErc20Delegate.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./CErc20.sol";
import "./CDelegateInterface.sol";

/**
 * @title Compound's CErc20Delegate Contract
 * @notice CTokens which wrap an EIP-20 underlying and are delegated to
 * @author Compound
 */
contract CErc20Delegate is CDelegateInterface, CErc20 {
  /**
   * @notice Construct an empty delegate
   */
  constructor() {}

  /**
   * @notice Called by the delegator on a delegate to initialize it for duty
   * @param data The encoded bytes data for any initialization
   */
  function _becomeImplementation(bytes calldata data) external virtual override {
    require(msg.sender == address(this) || hasAdminRights(), "!self");

    // Make sure admin storage is set up correctly
    __admin = payable(0);
    __adminHasRights = false;
    __fuseAdminHasRights = false;
  }

  /**
   * @notice Called by the delegator on a delegate to forfeit its responsibility
   */
  function _resignImplementation() internal virtual {
    // Shh -- we don't ever want this hook to be marked pure
    if (false) {
      implementation = address(0);
    }
  }

  /**
   * @dev Internal function to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param allowResign Flag to indicate whether to call _resignImplementation on the old implementation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationInternal(
    address implementation_,
    bool allowResign,
    bytes memory becomeImplementationData
  ) internal {
    // Check whitelist
    require(
      IFuseFeeDistributor(fuseAdmin).cErc20DelegateWhitelist(implementation, implementation_, allowResign),
      "!impl"
    );

    // Call _resignImplementation internally (this delegate's code)
    if (allowResign) _resignImplementation();

    // Get old implementation
    address oldImplementation = implementation;

    // Store new implementation
    implementation = implementation_;

    // Call _becomeImplementation externally (delegating to new delegate's code)
    _functionCall(
      address(this),
      abi.encodeWithSignature("_becomeImplementation(bytes)", becomeImplementationData),
      "!become"
    );

    // Emit event
    emit NewImplementation(oldImplementation, implementation);
  }

  /**
   * @notice Called by the admin to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param allowResign Flag to indicate whether to call _resignImplementation on the old implementation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationSafe(
    address implementation_,
    bool allowResign,
    bytes calldata becomeImplementationData
  ) external override {
    // Check admin rights
    require(hasAdminRights(), "!admin");

    // Set implementation
    _setImplementationInternal(implementation_, allowResign, becomeImplementationData);
  }

  /**
   * @notice Function called before all delegator functions
   * @dev Checks comptroller.autoImplementation and upgrades the implementation if necessary
   */
  function _prepare() external payable override {
    if (msg.sender != address(this) && ComptrollerV3Storage(address(comptroller)).autoImplementation()) {
      (address latestCErc20Delegate, bool allowResign, bytes memory becomeImplementationData) = IFuseFeeDistributor(
        fuseAdmin
      ).latestCErc20Delegate(implementation);
      if (implementation != latestCErc20Delegate)
        _setImplementationInternal(latestCErc20Delegate, allowResign, becomeImplementationData);
    }
  }
}

contracts/compound/CDelegateInterface.so.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

contract CDelegationStorage {
  /**
   * @notice Implementation address for this contract
   */
  address public implementation;
}

abstract contract CDelegateInterface is CDelegationStorage {
  /**
   * @notice Emitted when implementation is changed
   */
  event NewImplementation(address oldImplementation, address newImplementation);

  /**
   * @notice Called by the admin to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param allowResign Flag to indicate whether to call _resignImplementation on the old implementation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationSafe(
    address implementation_,
    bool allowResign,
    bytes calldata becomeImplementationData
  ) external virtual;

  /**
   * @notice Called by the delegator on a delegate to initialize it for duty
   * @dev Should revert if any issues arise which make it unfit for delegation
   * @param data The encoded bytes data for any initialization
   */
  function _becomeImplementation(bytes calldata data) external virtual;

  /**
   * @notice Function called before all delegator functions
   * @dev Checks comptroller.autoImplementation and upgrades the implementation if necessary
   */
  function _prepare() external payable virtual;
}

