pragma solidity >=0.8.0;

// https://raw.githubusercontent.com/Uniswap/uniswap-v2-core/master/contracts/libraries/UQ112x112.sol
// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))

// range: [0, 2**112 - 1]
// resolution: 1 / 2**112

library UQ112x112 {
  uint224 constant Q112 = 2**112;

  // encode a uint112 as a UQ112x112
  function encode(uint112 y) internal pure returns (uint224 z) {
    z = uint224(y) * Q112; // never overflows
  }

  // divide a UQ112x112 by a uint112, returning a UQ112x112
  function uqdiv(uint224 x, uint112 y) internal pure returns (uint224 z) {
    z = x / uint224(y);
  }
}

contracts/oracles/default/PreferredPrice.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "../../external/compound/IPriceOracle.sol";
import "../../external/compound/ICToken.sol";
import "../../external/compound/ICErc20.sol";

import "../BasePriceOracle.sol";
import "../MasterPriceOracle.sol";
import "../default/ChainlinkPriceOracleV2.sol";

/**
 * @title PreferredPriceOracle
 * @notice Returns prices from MasterPriceOracle, ChainlinkPriceOracleV2, or prices from a tertiary oracle (in order of preference).
 * @dev Implements `PriceOracle` and `BasePriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract PreferredPriceOracle is IPriceOracle, BasePriceOracle {
  /**
   * @dev The primary `MasterPriceOracle`.
   */
  MasterPriceOracle public masterOracle;

  /**
   * @dev The secondary `ChainlinkPriceOracleV2`.
   */
  ChainlinkPriceOracleV2 public chainlinkOracleV2;

  /**
   * @dev The tertiary `PriceOracle`.
   */
  IPriceOracle public tertiaryOracle;

  /**
   * @dev The Wrapped native asset address.
   */
  address public wtoken;

  /**
   * @dev Constructor to set the primary `MasterPriceOracle`, the secondary `ChainlinkPriceOracleV2`, and the tertiary `PriceOracle`.
   */
  constructor(
    MasterPriceOracle _masterOracle,
    ChainlinkPriceOracleV2 _chainlinkOracleV2,
    IPriceOracle _tertiaryOracle,
    address _wtoken
  ) {
    require(address(_masterOracle) != address(0), "MasterPriceOracle not set.");
    require(address(_chainlinkOracleV2) != address(0), "ChainlinkPriceOracleV2 not set.");
    require(address(_tertiaryOracle) != address(0), "Tertiary price oracle not set.");
    masterOracle = _masterOracle;
    chainlinkOracleV2 = _chainlinkOracleV2;
    tertiaryOracle = _tertiaryOracle;
    wtoken = _wtoken;
  }

  /**
   * @notice Fetches the token/ETH price, with 18 decimals of precision.
   * @param underlying The underlying token address for which to get the price.
   * @return Price denominated in ETH (scaled by 1e18)
   */
  function price(address underlying) external view override returns (uint256) {
    // Return 1e18 for wtoken
    if (underlying == wtoken) return 1e18;

    // Try to get MasterPriceOracle price
    if (address(masterOracle.oracles(underlying)) != address(0)) return masterOracle.price(underlying);

    // Try to get ChainlinkPriceOracleV2 price
    if (address(chainlinkOracleV2.priceFeeds(underlying)) != address(0)) return chainlinkOracleV2.price(underlying);

    // Otherwise, get price from tertiary oracle
    return BasePriceOracle(address(tertiaryOracle)).price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Fuse pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICToken cToken) external view override returns (uint256) {
    // Return 1e18 for ETH
    if (cToken.isCEther()) return 1e18;

    // Get underlying ERC20 token address
    address underlying = address(ICErc20(address(cToken)).underlying());

    // Return 1e18 for wtoken
    if (underlying == wtoken) return 1e18;

    // Try to get MasterPriceOracle price
    if (address(masterOracle.oracles(underlying)) != address(0)) return masterOracle.getUnderlyingPrice(cToken);

    // Try to get ChainlinkPriceOracleV2 price
    if (address(chainlinkOracleV2.priceFeeds(underlying)) != address(0))
      return chainlinkOracleV2.getUnderlyingPrice(cToken);

    // Otherwise, get price from tertiary oracle
    return tertiaryOracle.getUnderlyingPrice(cToken);
  }
}

contracts/oracles/default/ChainlinkPrice.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";

import "../../external/compound/IPriceOracle.sol";
import "../../external/compound/ICToken.sol";
import "../../external/compound/ICErc20.sol";

import "../../external/chainlink/AggregatorV3Interface.sol";

import "../BasePriceOracle.sol";

/**
 * @title ChainlinkPriceOracleV2
 * @notice Returns prices from Chainlink.
 * @dev Implements `PriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract ChainlinkPriceOracleV2 is IPriceOracle, BasePriceOracle {
  /**
   * @notice Maps ERC20 token addresses to ETH-based Chainlink price feed contracts.
   */
  mapping(address => AggregatorV3Interface) public priceFeeds;

  /**
   * @notice Maps ERC20 token addresses to enums indicating the base currency of the feed.
   */
  mapping(address => FeedBaseCurrency) public feedBaseCurrencies;

  /**
   * @notice Enum indicating the base currency of a Chainlink price feed.
   */
  enum FeedBaseCurrency {
    ETH,
    USD
  }

  /**
   * @notice Chainlink NATIVE/USD price feed contracts.
   */
  AggregatorV3Interface public immutable NATIVE_TOKEN_USD_PRICE_FEED;

  /**
   * @dev The administrator of this `MasterPriceOracle`.
   */
  address public admin;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool public canAdminOverwrite;

  /**
   * @dev The Wrapped native asset address.
   */
  address public immutable wtoken;

  /**
   * @dev Constructor to set admin and canAdminOverwrite, wtoken address and native token USD price feed address
   */
  constructor(
    address _admin,
    bool _canAdminOverwrite,
    address _wtoken,
    address nativeTokenUsd
  ) {
    admin = _admin;
    canAdminOverwrite = _canAdminOverwrite;
    wtoken = _wtoken;
    NATIVE_TOKEN_USD_PRICE_FEED = AggregatorV3Interface(nativeTokenUsd);
  }

  /**
   * @dev Changes the admin and emits an event.
   */
  function changeAdmin(address newAdmin) external onlyAdmin {
    address oldAdmin = admin;
    admin = newAdmin;
    emit NewAdmin(oldAdmin, newAdmin);
  }

  /**
   * @dev Event emitted when `admin` is changed.
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  /**
   * @dev Modifier that checks if `msg.sender == admin`.
   */
  modifier onlyAdmin() {
    require(msg.sender == admin, "Sender is not the admin.");
    _;
  }

  /**
   * @dev Admin-only function to set price feeds.
   * @param underlyings Underlying token addresses for which to set price feeds.
   * @param feeds The Chainlink price feed contract addresses for each of `underlyings`.
   * @param baseCurrency The currency in which `feeds` are based.
   */
  function setPriceFeeds(
    address[] memory underlyings,
    AggregatorV3Interface[] memory feeds,
    FeedBaseCurrency baseCurrency
  ) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == feeds.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // For each token/feed
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];

      // Check for existing oracle if !canAdminOverwrite
      if (!canAdminOverwrite)
        require(
          address(priceFeeds[underlying]) == address(0),
          "Admin cannot overwrite existing assignments of price feeds to underlying tokens."
        );

      // Set feed and base currency
      priceFeeds[underlying] = feeds[i];
      feedBaseCurrencies[underlying] = baseCurrency;
    }
  }

  /**
   * @dev Internal function returning the price in ETH of `underlying`.
   */
  function _price(address underlying) internal view returns (uint256) {
    // Return 1e18 for WTOKEN
    if (underlying == wtoken || underlying == address(0)) return 1e18;

    // Get token/ETH price from Chainlink
    AggregatorV3Interface feed = priceFeeds[underlying];
    require(address(feed) != address(0), "No Chainlink price feed found for this underlying ERC20 token.");
    FeedBaseCurrency baseCurrency = feedBaseCurrencies[underlying];

    if (baseCurrency == FeedBaseCurrency.ETH) {
      (, int256 tokenEthPrice, , , ) = feed.latestRoundData();
      return tokenEthPrice >= 0 ? (uint256(tokenEthPrice) * 1e18) / (10**uint256(feed.decimals())) : 0;
    } else if (baseCurrency == FeedBaseCurrency.USD) {
      (, int256 nativeTokenUsdPrice, , , ) = NATIVE_TOKEN_USD_PRICE_FEED.latestRoundData();
      if (nativeTokenUsdPrice <= 0) return 0;
      (, int256 tokenUsdPrice, , , ) = feed.latestRoundData();
      return
        tokenUsdPrice >= 0
          ? ((uint256(tokenUsdPrice) * 1e26) / (10**uint256(feed.decimals()))) / uint256(nativeTokenUsdPrice)
          : 0;
    }
  }

  /**
   * @dev Returns the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Fuse pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICToken cToken) external view override returns (uint256) {
    // Return 1e18 for ETH
    if (cToken.isCEther()) return 1e18;

    // Get underlying token address
    address underlying = ICErc20(address(cToken)).underlying();

    // Get price
    uint256 chainlinkPrice = _price(underlying);

    // Format and return price
    uint256 underlyingDecimals = uint256(ERC20Upgradeable(underlying).decimals());
    return
      underlyingDecimals <= 18
        ? uint256(chainlinkPrice) * (10**(18 - underlyingDecimals))
        : uint256(chainlinkPrice) / (10**(underlyingDecimals - 18));
  }
}

contracts/external/chainlink/AggregatorV.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

interface AggregatorV3Interface {
  function decimals() external view returns (uint8);

  function description() external view returns (string memory);

  function version() external view returns (uint256);

  // getRoundData and latestRoundData should both raise "No data present"
  // if they do not have data to report, instead of returning unset values
  // which could be misinterpreted as actual reported values.
  function getRoundData(uint80 _roundId)
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );

  function latestRoundData()
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );
}

contracts/oracles/mainnet/BadgerPriceOra.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";

import "../../external/compound/IPriceOracle.sol";
import "../../external/compound/ICErc20.sol";

import "../../external/chainlink/AggregatorV3Interface.sol";

import "../../external/badger/IXToken.sol";
import "../../external/badger/IDigg.sol";
import "../../external/badger/DiggSett.sol";

import "../BasePriceOracle.sol";

/**
 * @title BadgerPriceOracle
 * @notice Returns prices for bDIGG, bBADGER, and ibBTC.
 * @dev Implements `PriceOracle` and `BasePriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract BadgerPriceOracle is IPriceOracle, BasePriceOracle {
  /**
   * @dev BADGER/ETH Chainlink price feed.
   */
  AggregatorV3Interface public constant BADGER_ETH_FEED =
    AggregatorV3Interface(0x58921Ac140522867bf50b9E009599Da0CA4A2379);

  /**
   * @dev bBADGER ERC20 token contract.
   */
  IXToken public constant BBADGER = IXToken(0x19D97D8fA813EE2f51aD4B4e04EA08bAf4DFfC28);

  /**
   * @dev DIGG/BTC Chainlink price feed.
   */
  AggregatorV3Interface public constant DIGG_BTC_FEED =
    AggregatorV3Interface(0x418a6C98CD5B8275955f08F0b8C1c6838c8b1685);

  /**
   * @dev bDIGG ERC20 token contract.
   */
  DiggSett public constant BDIGG = DiggSett(0x7e7E112A68d8D2E221E11047a72fFC1065c38e1a);

  /**
   * @dev BTC/ETH Chainlink price feed.
   */
  AggregatorV3Interface public constant BTC_ETH_FEED =
    AggregatorV3Interface(0xdeb288F737066589598e9214E782fa5A8eD689e8);

  /**
   * @dev ibBTC ERC20 token contract.
   */
  IXToken public constant IBBTC = IXToken(0xc4E15973E6fF2A35cC804c2CF9D2a1b817a8b40F);

  /**
   * @notice Fetches the token/ETH price, with 18 decimals of precision.
   * @param underlying The underlying token address for which to get the price.
   * @return Price denominated in ETH (scaled by 1e18)
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Fuse pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICToken cToken) external view override returns (uint256) {
    address underlying = ICErc20(address(cToken)).underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10**uint256(ERC20Upgradeable(underlying).decimals()));
  }

  /**
   * @notice Fetches the token/ETH price, with 18 decimals of precision.
   */
  function _price(address token) internal view returns (uint256) {
    if (token == address(BBADGER)) {
      (uint80 roundId, int256 badgerEthPrice, , , uint80 answeredInRound) = BADGER_ETH_FEED.latestRoundData();
      require(answeredInRound == roundId, "Chainlink round timed out.");
      return badgerEthPrice > 0 ? (uint256(badgerEthPrice) * BBADGER.getPricePerFullShare()) / 1e18 : 0;
    } else if (token == address(BDIGG)) {
      (uint80 roundId, int256 diggBtcPrice, , , uint80 answeredInRound) = DIGG_BTC_FEED.latestRoundData();
      require(answeredInRound == roundId, "Chainlink round timed out.");
      if (diggBtcPrice < 0) return 0;
      int256 btcEthPrice;
      (roundId, btcEthPrice, , , answeredInRound) = BTC_ETH_FEED.latestRoundData();
      require(answeredInRound == roundId, "Chainlink round timed out.");
      if (btcEthPrice < 0) return 0;
      // SafeMath removal refactoring: multiplication before division for higher precision
      uint256 bDiggDiggPrice = IDigg(BDIGG.token()).sharesToFragments((BDIGG.shares() * 1e18) / BDIGG.totalSupply());
      // bDIGG/ETH price = (bDIGG/DIGG price / 1e9) * (DIGG/BTC price / 1e8) * BTC/ETH price
      // Divide by BTC base unit 1e8 (BTC has 8 decimals) and DIGG base unit 1e9 (DIGG has 9 decimals)
      return bDiggDiggPrice > 0 ? (((uint256(diggBtcPrice) * uint256(btcEthPrice)) / 1e8) * bDiggDiggPrice) / 1e9 : 0;
    } else if (token == address(IBBTC)) {
      (uint80 roundId, int256 btcEthPrice, , , uint80 answeredInRound) = BTC_ETH_FEED.latestRoundData();
      require(answeredInRound == roundId, "Chainlink round timed out.");
      return btcEthPrice > 0 ? (uint256(btcEthPrice) * IBBTC.pricePerShare()) / 1e18 : 0;
    } else revert("Invalid token address passed to BadgerPriceOracle.");
  }
}

