// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

import "../external/compound/ICErc20.sol";

import "./IRedemptionStrategy.sol";

/**
 * @title CErc20Liquidator
 * @notice Redeems seized Compound/Cream/Fuse CErc20 cTokens for underlying tokens for use as a step in a liquidation.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract CErc20Liquidator is IRedemptionStrategy {
  /**
   * @notice Redeems custom collateral `token` for an underlying token.
   * @param inputToken The input wrapped token to be redeemed for an underlying token.
   * @param inputAmount The amount of the input wrapped token to be redeemed for an underlying token.
   * @param strategyData The ABI-encoded data to be used in the redemption strategy logic.
   * @return outputToken The underlying ERC20 token outputted.
   * @return outputAmount The quantity of underlying tokens outputted.
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external override returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    // Redeem cErc20 for underlying ERC20 token (and store output as new collateral)
    ICErc20 cErc20 = ICErc20(address(inputToken));
    uint256 redeemResult = cErc20.redeem(inputAmount);
    require(redeemResult == 0, "Error calling redeeming seized cErc20: error code not equal to 0");
    outputToken = IERC20Upgradeable(cErc20.underlying());
    outputAmount = outputToken.balanceOf(address(this));
  }
}

contracts/oracles/1337/SimplePriceOracle.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "../../compound/PriceOracle.sol";
import "../../compound/CErc20.sol";

contract SimplePriceOracle is PriceOracle {
  mapping(address => uint256) prices;
  event PricePosted(
    address asset,
    uint256 previousPriceMantissa,
    uint256 requestedPriceMantissa,
    uint256 newPriceMantissa
  );

  function getUnderlyingPrice(CToken cToken) public view override returns (uint256) {
    if (compareStrings(cToken.symbol(), "cETH")) {
      return 1e18;
    } else {
      return prices[address(CErc20(address(cToken)).underlying())];
    }
  }

  function setUnderlyingPrice(CToken cToken, uint256 underlyingPriceMantissa) public {
    address asset = address(CErc20(address(cToken)).underlying());
    emit PricePosted(asset, prices[asset], underlyingPriceMantissa, underlyingPriceMantissa);
    prices[asset] = underlyingPriceMantissa;
  }

  function setDirectPrice(address asset, uint256 price) public {
    emit PricePosted(asset, prices[asset], price, price);
    prices[asset] = price;
  }

  function price(address underlying) external view returns (uint256) {
    return prices[address(underlying)];
  }

  // v1 price oracle interface for use as backing of proxy
  function assetPrices(address asset) external view returns (uint256) {
    return prices[asset];
  }

  function compareStrings(string memory a, string memory b) internal pure returns (bool) {
    return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
  }
}

contracts/compound/CEtherDelegate.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./CEther.sol";
import "./CDelegateInterface.sol";

/**
 * @title Compound's CEtherDelegate Contract
 * @notice CTokens which wrap Ether and are delegated to
 * @author Compound
 */
contract CEtherDelegate is CDelegateInterface, CEther {
  /**
   * @notice Construct an empty delegate
   */
  constructor() {}

  /**
   * @notice Called by the delegator on a delegate to initialize it for duty
   * @param data The encoded bytes data for any initialization
   */
  function _becomeImplementation(bytes calldata data) external override {
    require(msg.sender == address(this) || hasAdminRights(), "!self");

    // Make sure admin storage is set up correctly
    __admin = payable(0);
    __adminHasRights = false;
    __fuseAdminHasRights = false;
  }

  /**
   * @notice Called by the delegator on a delegate to forfeit its responsibility
   */
  function _resignImplementation() internal {
    // Shh -- we don't ever want this hook to be marked pure
    if (false) {
      implementation = address(0);
    }
  }

  /**
   * @dev Internal function to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param allowResign Flag to indicate whether to call _resignImplementation on the old implementation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationInternal(
    address implementation_,
    bool allowResign,
    bytes memory becomeImplementationData
  ) internal {
    // Check whitelist
    require(
      IFuseFeeDistributor(fuseAdmin).cEtherDelegateWhitelist(implementation, implementation_, allowResign),
      "!impl"
    );

    // Call _resignImplementation internally (this delegate's code)
    if (allowResign) _resignImplementation();

    // Get old implementation
    address oldImplementation = implementation;

    // Store new implementation
    implementation = implementation_;

    // Call _becomeImplementation externally (delegating to new delegate's code)
    _functionCall(
      address(this),
      abi.encodeWithSignature("_becomeImplementation(bytes)", becomeImplementationData),
      "!become"
    );

    // Emit event
    emit NewImplementation(oldImplementation, implementation);
  }

  /**
   * @notice Called by the admin to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param allowResign Flag to indicate whether to call _resignImplementation on the old implementation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationSafe(
    address implementation_,
    bool allowResign,
    bytes calldata becomeImplementationData
  ) external override {
    // Check admin rights
    require(hasAdminRights(), "!admin");

    // Set implementation
    _setImplementationInternal(implementation_, allowResign, becomeImplementationData);
  }

  /**
   * @notice Function called before all delegator functions
   * @dev Checks comptroller.autoImplementation and upgrades the implementation if necessary
   */
  function _prepare() external payable override {
    if (msg.sender != address(this) && ComptrollerV3Storage(address(comptroller)).autoImplementation()) {
      (address latestCEtherDelegate, bool allowResign, bytes memory becomeImplementationData) = IFuseFeeDistributor(
        fuseAdmin
      ).latestCEtherDelegate(implementation);
      if (implementation != latestCEtherDelegate)
        _setImplementationInternal(latestCEtherDelegate, allowResign, becomeImplementationData);
    }
  }
}

